import React, { useState } from 'react';

const PerDiemCalculator = () => {
  // Allow users to enter a city, state, and optionally a ZIP code.
  const [jobLocation, setJobLocation] = useState({ city: '', state: '', zip: '' });
  const [stipendData, setStipendData] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  // Use your valid API key here.
  const GSA_API_KEY = "BP2fPChQmadJd3ADlMYADSJqXT8CxFD6LZQh4KHt";
  const fiscalYear = "2025";  // Set the fiscal year to 2025

  const fetchStipendRates = async () => {
    setLoading(true);
    try {
      let apiUrl = "";

      // Determine which endpoint to call based on user input.
      if (jobLocation.zip) {
        // Use ZIP code endpoint
        apiUrl = `https://api.gsa.gov/travel/perdiem/v2/rates/zip/${jobLocation.zip}/year/${fiscalYear}`;
      } else if (jobLocation.city && jobLocation.state) {
        // Use city/state endpoint
        apiUrl = `https://api.gsa.gov/travel/perdiem/v2/rates/city/${jobLocation.city}/state/${jobLocation.state}/year/${fiscalYear}`;
      } else {
        setError("Please provide either a ZIP code or both city and state.");
        setLoading(false);
        return;
      }

      // Fetch data with the API key in the header.
      const response = await fetch(apiUrl, {
        headers: {
          "X-API-KEY": GSA_API_KEY
        }
      });

      const result = await response.json();
      const rates = result?.rates?.[0];

      if (rates) {
        setStipendData({
          lodging: parseFloat(rates.lodging) || 0,
          mie: parseFloat(rates.mie) || 0
        });
        setError(null);
      } else {
        setStipendData(null);
        setError("No data found for this location.");
      }
    } catch (err) {
      console.error("Error fetching rates:", err);
      setStipendData(null);
      setError("Failed to fetch data.");
    }
    setLoading(false);
  };

  const formatCurrency = (value) => `$${value.toFixed(2)}`;

  return (
    <div>
      <h2>üìç Per Diem Calculator</h2>
      <div>
        <label>City</label>
        <input
          type="text"
          value={jobLocation.city}
          onChange={(e) => setJobLocation({ ...jobLocation, city: e.target.value })}
        />
        <label>State (2-letter)</label>
        <input
          type="text"
          value={jobLocation.state}
          onChange={(e) => setJobLocation({ ...jobLocation, state: e.target.value.toUpperCase() })}
        />
        <label>ZIP Code (Optional)</label>
        <input
          type="text"
          value={jobLocation.zip}
          onChange={(e) => setJobLocation({ ...jobLocation, zip: e.target.value })}
        />
      </div>
      <button onClick={fetchStipendRates}>Fetch GSA Rates</button>
      {loading && <p>Loading...</p>}
      {stipendData && (
        <div>
          <p>üè† Housing: {formatCurrency(stipendData.lodging)} √ó 7 = {formatCurrency(stipendData.lodging * 7)}</p>
          <p>üçΩÔ∏è M&IE: {formatCurrency(stipendData.mie)} √ó 7 = {formatCurrency(stipendData.mie * 7)}</p>
          <p>üí∞ Total Weekly: {formatCurrency((stipendData.lodging + stipendData.mie) * 7)}</p>
        </div>
      )}
      {error && <p>{error}</p>}
    </div>
  );
};

export default PerDiemCalculator;
