import React, { useState } from 'react';

const PerDiemCalculator = () => {
  const [jobLocation, setJobLocation] = useState({ city: '', state: '' });
  const [stipendData, setStipendData] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const GSA_API_KEY = "YOUR_GSA_API_KEY";  // Replace with your GSA API key

  const fetchStipendRates = async (city, state) => {
    setLoading(true);
    try {
      const apiUrl = `https://api.gsa.gov/travel/perdiem/v2/rates/${city}/${state}?api_key=${GSA_API_KEY}`;
      const response = await fetch(apiUrl);
      const result = await response.json();
      const rates = result?.rates[0];

      if (rates) {
        setStipendData({ lodging: rates.lodging, mie: rates.mie });
        setError(null);
      } else {
        setError("No data found for this location.");
      }
    } catch (err) {
      setError("Failed to fetch data.");
    }
    setLoading(false);
  };

  const formatCurrency = (value) => `$${value.toFixed(2)}`;

  return (
    <div>
      <h2>üìç Per Diem Calculator</h2>
      <div>
        <label>City</label>
        <input 
          type="text" 
          value={jobLocation.city} 
          onChange={(e) => setJobLocation({ ...jobLocation, city: e.target.value })} 
        />
        <label>State (2-letter)</label>
        <input 
          type="text" 
          value={jobLocation.state} 
          onChange={(e) => setJobLocation({ ...jobLocation, state: e.target.value.toUpperCase() })} 
        />
      </div>
      <button onClick={() => fetchStipendRates(jobLocation.city, jobLocation.state)}>Fetch GSA Rates</button>
      {stipendData && (
        <div>
          <p>üè† Housing: {formatCurrency(stipendData.lodging)} √ó 7 = {formatCurrency(stipendData.lodging * 7)}</p>
          <p>üçΩÔ∏è Meals & Incidentals: {formatCurrency(stipendData.mie)} √ó 7 = {formatCurrency(stipendData.mie * 7)}</p>
          <p>üí∞ Total Weekly Stipend: {formatCurrency((stipendData.lodging + stipendData.mie) * 7)}</p>
        </div>
      )}
      {error && <p>{error}</p>}
    </div>
  );
};

export default PerDiemCalculator;
