import React, { useState } from "react";

const PerDiemCalculator = () => {
  const [jobLocation, setJobLocation] = useState({ city: "", state: "", zip: "" });
  const [currentRate, setCurrentRate] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  // Your GSA API key and fiscal year 2025
  const GSA_API_KEY = "BP2fPChQmadJd3ADlMYADSJqXT8CxFD6LZQh4KHt";
  const fiscalYear = "2025";

  const fetchCurrentRates = async () => {
    setLoading(true);
    setError(null);
    setCurrentRate(null);

    let apiUrl = "";
    if (jobLocation.zip) {
      // Use ZIP code endpoint
      apiUrl = `https://api.gsa.gov/travel/perdiem/v2/rates/zip/${jobLocation.zip}/year/${fiscalYear}`;
    } else if (jobLocation.city && jobLocation.state) {
      // Use city/state endpoint
      apiUrl = `https://api.gsa.gov/travel/perdiem/v2/rates/city/${jobLocation.city}/state/${jobLocation.state}/year/${fiscalYear}`;
    } else {
      setError("Please provide either a ZIP code or both city and state.");
      setLoading(false);
      return;
    }

    try {
      const response = await fetch(apiUrl, {
        headers: {
          "X-API-KEY": GSA_API_KEY,
        },
      });
      const result = await response.json();
      const rates = result?.rates;
      if (!rates || rates.length === 0) {
        setError("No data found for this location/year.");
      } else {
        // Get current month from today's date (1-12)
        const currentMonth = new Date().getMonth() + 1;
        // Find the matching rate based on the month
        const matchingRate = rates.find(rate => parseInt(rate.month) === currentMonth);
        if (!matchingRate) {
          setError(`No data found for the current month (${currentMonth}).`);
        } else {
          setCurrentRate(matchingRate);
        }
      }
    } catch (err) {
      console.error(err);
      setError("Failed to fetch data.");
    }
    setLoading(false);
  };

  const formatCurrency = (value) => `$${parseFloat(value).toFixed(2)}`;

  return (
    <div>
      <h2>üìç Per Diem Calculator</h2>
      <div>
        <label>City</label>
        <input
          type="text"
          value={jobLocation.city}
          onChange={(e) => setJobLocation({ ...jobLocation, city: e.target.value })}
        />
        <label>State (2-letter)</label>
        <input
          type="text"
          value={jobLocation.state}
          onChange={(e) => setJobLocation({ ...jobLocation, state: e.target.value.toUpperCase() })}
        />
        <label>ZIP Code (Optional)</label>
        <input
          type="text"
          value={jobLocation.zip}
          onChange={(e) => setJobLocation({ ...jobLocation, zip: e.target.value })}
        />
      </div>
      <button onClick={fetchCurrentRates}>Fetch Current GSA Rates</button>
      {loading && <p>Loading...</p>}
      {currentRate && (
        <div>
          <p>
            For Month {currentRate.month}:<br />
            üè† Housing (Daily): {formatCurrency(currentRate.value)}<br />
            üçΩÔ∏è M&IE (Daily): {formatCurrency(currentRate.meals)}<br />
            üí∞ Total Weekly: {formatCurrency((parseFloat(currentRate.value) + parseFloat(currentRate.meals)) * 7)}
          </p>
        </div>
      )}
      {error && <p>{error}</p>}
    </div>
  );
};

export default PerDiemCalculator;
